#!/usr/bin/env python3

print('13) Composição de Nucleotídeos. Escreva um script que:')

seq =''
myset = set(seq)
print('determine os caracteres únicos nesta sequência')
print(myset)
print('')
print('Itere sobre cada caractere único e conte o número encontrado na sequência')

contagem = {}
for n in myset: # [A C T G]
    if n in seq:
        print(f'{n}: {seq.count(n)}')
        contagem[n] = seq.count(n)
print('')
print('Calcule e relate a composição de nucleotídeos e o conteúdo de GC.')
cc = seq.count('C')
gg = seq.count('G')
p = (cc+gg)/len(seq)
print(contagem)
print(f'Conteudo CG: {p*100}%')
